mapper.update:
update article
set
article_title = #{articleTitle}, article_content = #{articleContent}
where article_no = #{articleNo}

mapper.select:
select article_no, user_id, article_title, article_content, article_status, article_hit, article_write_time, case when timestampdiff(YEAR, article_write_time, current_timestamp) then concat(timestampdiff(YEAR, article_write_time, current_timestamp), "년전") when timestampdiff(MONTH, article_write_time, current_timestamp) then concat(timestampdiff(MONTH, article_write_time, current_timestamp), "달전") when timestampdiff(DAY, article_write_time, current_timestamp) then concat(timestampdiff(DAY, article_write_time, current_timestamp), "일전") when timestampdiff(HOUR, article_write_time, current_timestamp) then concat(timestampdiff(HOUR, article_write_time, current_timestamp), "시간전") when timestampdiff(MINUTE, article_write_time, current_timestamp) then concat(timestampdiff(MINUTE, article_write_time, current_timestamp), "분전") when timestampdiff(SECOND, article_write_time, current_timestamp) then concat(timestampdiff(SECOND, article_write_time, current_timestamp), "초전") end article_write_time_ago from article where article_status = #{articleStatus}
and 1 = 0
or article_status = 0
order by article_write_time desc limit #{start}, #{spp}
